from PIL import Image

def create_binary_pattern_image(binary_data, width=32, height=18, output_path="output_pattern.png"):
    # 画像の初期化（白背景で全ピクセルを白に設定）
    img = Image.new('RGB', (width, height), color=(55, 55, 255))  # 'RGB'モードで白背景

    # バイナリデータを画像のピクセルに反映
    pixels = img.load()
    for i, bit in enumerate(binary_data):
        if i >= width * height:
            break  # 576ピクセルを超えた場合は終了
        row = i // width
        col = i % width
        color = (255, 0, 0) if bit == '1' else (55, 55, 255)  # 1は赤、0は白
        pixels[col, row] = color

    # 画像を保存
    img = img.resize((width * 10, height * 10), Image.NEAREST)  # 拡大して見やすくする
    img.save(output_path)
    print(f"画像を保存しました: {output_path}")

# 使用例
binary_data = ""
create_binary_pattern_image(binary_data)
